jQuery1_11_0(function($) {
	//메인슬라이드
	$('#fixoSlide .box-image').each(function(index){
		var index = index+1, 
			url = $(this).find('img').attr('src');
		$(this).parent().css('background', 'url('+url+') no-repeat center');
	});
	var delta = 100;
	var timer = null;
	clearTimeout(timer);
	timer = setTimeout(mainSlide, delta);
	function mainSlide() {
		$("#fixoSlide .box-slider").on('init', function(event, slick){
			$('#fixoSlide').delay(400).animate({opacity: 1});
		});
		var slider = $("#fixoSlide .box-slider").slick({
			fade: true, //페이드인 false 시 좌우 슬라이드
			autoplay: true, //자동슬라이드
			autoplaySpeed: 7000, //슬라이드딜레이
			speed: 1000, //변환속도
			infinite: true, //반복
			appendArrows: $('#fixoSlide .arrow-wrap'), //arrow 위치지정
			arrows: true, //좌우버튼
			//appendDots: $('.dots-wrap'), //dots 위치지정
			dots: false, //현재위치
			initialSlide: 0, //시작슬라이드
			accessibility: false, //키보드
			vertical: false, //수직슬라이드
			verticalSwiping: false, //swiping 방향 수직으로
			pauseOnDotsHover: true, //dot에 마우스오버시 autoplay 멈춤
			pauseOnFocus: false, //slider 선택 시(focus) autoplay 멈춤
			pauseOnHover: false, //slider에 마우스오버시 autoplay 멈춤
			draggable: false, //desktop dragging
			adaptiveHeight: false, //콘텐츠높이에맞게자동높이
			centerMode: false, //이전다음슬라이드가 있을때 좌우에 보이기
			centerPadding: '100px', //좌우보이는영역
			slidesToShow: 1, //화면에보이는슬라이드개수
			slidesToScroll: 1, //스크롤되는슬라이드개수
			touchThreshold: 20, //터치거리(1/touchThreshold)
		});
		slider.on('touchmove', function(){ //모바일touchmove autoplay 계속되게
			slider.slick('slickPlay');
		});

		progressbar();
		function progressbar(){
			var time = 5; //자동변환 딜레이
			var $bar,
				isPause,
				tick,
				percentTime;
			$bar = $('#fixoSlide .slider-progress .progress');

			function startProgressbar() {
				resetProgressbar();
				percentTime = 0;
				isPause = false;
				tick = setInterval(interval, 10);
			}
			function interval() {
				if(isPause === false) {
					percentTime += 1 / (time+0.1);
					$bar.css({
						width: percentTime+"%"
					});
					if(percentTime >= 100){
						slider.slick('slickNext');
						startProgressbar();
					}
				}
			}
			function resetProgressbar() {
				$bar.css({
					width: 0+'%' 
				});
				clearTimeout(tick);
			}
			startProgressbar();
			slider.on('beforeChange', function(){
				resetProgressbar();
				startProgressbar();
			});
		}
	}

	$("#fixoSlide2 .box-slider").on('init', function(event, slick){
		$('#fixoSlide2').delay(400).animate({opacity: 1});
	});
	var slider2 = $("#fixoSlide2 .box-slider").slick({
		fade: false, //페이드인 false 시 좌우 슬라이드
		autoplay: false, //자동슬라이드
		autoplaySpeed: 4000, //슬라이드딜레이
		speed: 800, //변환속도
		infinite: true, //반복
		appendArrows: $('#fixoSlide2 .arrow-wrap'), //arrow 위치지정
		arrows: true, //좌우버튼
		//appendDots: $('.dots-wrap'), //dots 위치지정
		dots: false, //현재위치
		initialSlide: 0, //시작슬라이드
		accessibility: false, //키보드
		vertical: false, //수직슬라이드
		verticalSwiping: false, //swiping 방향 수직으로
		pauseOnDotsHover: true, //dot에 마우스오버시 autoplay 멈춤
		pauseOnFocus: false, //slider 선택 시(focus) autoplay 멈춤
		pauseOnHover: false, //slider에 마우스오버시 autoplay 멈춤
		draggable: true, //desktop dragging
		adaptiveHeight: false, //콘텐츠높이에맞게자동높이
		centerMode: true, //이전다음슬라이드가 있을때 좌우에 보이기
		centerPadding: '0', //좌우보이는영역
		slidesToShow: 1, //화면에보이는슬라이드개수
		slidesToScroll: 1, //스크롤되는슬라이드개수
		touchThreshold: 20, //터치거리(1/touchThreshold)
		variableWidth: false
	});

	$('.main-layout .section2 .item-wrap .item .dt').not('.btn-more .dt').on('mouseenter', function(){
		$('.main-layout .section2 .item-wrap .item').removeClass('active');
		$(this).parent().addClass('active');
		var num = $(this).parent().index()+1;
		$('.main-layout .section2 .img-wrap .img').removeClass('active');
		$('.main-layout .section2 .img-wrap .img'+num).addClass('active');
	});

	$('.main-layout .section3 .slide-tab .item .item-inner').on('mouseenter', function(){
		$('.main-layout .section3 .slide-tab .item').removeClass('active');
		$(this).parent().addClass('active');
	});

	$('.main-layout .section4 .item-wrap .item').on('mouseenter', function(){
		$('.main-layout .section4 .item-wrap .item').removeClass('active');
		$(this).addClass('active');
		var num = $(this).index()+1;
		$('.main-layout .section4 .img-wrap .img').removeClass('active');
		$('.main-layout .section4 .img-wrap .img'+num).addClass('active');
	});
