//motion
$(document).ready(animate);
$(window).load(animate);
$(window).scroll(animate);
$(window).resize(animate);
function animate(){
	var delta = 500;
	var timer = null;
	clearTimeout(timer);
	timer = setTimeout(active, delta);

	function active() {
		var screenH = parseInt($(window).height());
		$('.fixo-motion').each(function(index){
			var setTop = parseInt($(this).offset().top), 
				setTopFix = setTop - screenH,
				setTopFix2 = setTop - (screenH/1.2),
				scrollTop = $(window).scrollTop(), 
				setBottonFix = setTop + $(this).height();
			
			if(setTopFix < scrollTop && setBottonFix > scrollTop){
				if(setTopFix2 < scrollTop){
					$('.fixo-delay', this).removeClass('fixo-nodelay').addClass('active');
				}
			} else {
				$('.fixo-delay', this).addClass('fixo-nodelay').removeClass('active');
			}
		});
	}
};

